-- Quantas vendas foram feitas com quantidade igual a 99 litros para o produto '1101035'?
SELECT COUNT(*) AS COUNT_QTD_99 FROM SUCOS_FRUTAS.DBO.ITENS_NOTAS_FISCAIS WHERE CODIGO_DO_PRODUTO = (SELECT CODIGO_DO_PRODUTO FROM SUCOS_FRUTAS.DBO.TABELA_DE_PRODUTOS WHERE NOME_DO_PRODUTO = 'Linha Refrescante - 1 Litro - Morango/Limao') AND QUANTIDADE = 99;